package miso;

public class ConditionalOperationExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	
		
		int score = 85;
		
		char grade ;
		
		if ( score > 90) {
			grade = 'A';
			
		} else if(score > 80){
			grade = 'B';
			
		}else {
			grade = 'c';
		}
		
		System.out.println(score +"is "+ grade);
		
		
		
		
		
		
		
		

	}

}
