package miso;

public class LengthExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String ssn = "9506241230123";
		int length = ssn.length();
		if(length ==13) {
			System.out.println("The number of digits of your resident registration number is correct.");
			
			
		}else {
			System.out.println("The number of digits of the resident registration number is incorrect.");
		}
		
		
		
		

	}

}
